from django.db import models# Create your models here.class Product(models.Model):	title = models.CharField(max_length=200, primary_key=True)	img = models.CharField(max_length=200)	description = models.CharField(max_length=500)	currency = models.CharField(max_length=50)	price = models.FloatField()	quantity = models.IntegerField()	total_price = models.FloatField()	def __str__(self):		return f'{self.title}'